// é€šç”¨CRUDè·¯ç”±ç”Ÿæˆå™¨
function createCRUDRoutes(resource, filename) {
    // è·å–æ‰€æœ‰èµ„æº
    app.get(`/api/${resource}`, async (req, res) => {
        try {
            const data = await readJSON(filename);
            res.json({ success: true, data });
        } catch (error) {
            res.status(500).json({ success: false, error: error.message });
        }
    });

    // è·å–å•ä¸ªèµ„æº
    app.get(`/api/${resource}/:id`, async (req, res) => {
        try {
            const data = await readJSON(filename);
            const item = data.find(item => String(item.id) === String(req.params.id));

            if (item) {
                res.json({ success: true, data: item });
            } else {
                res.status(404).json({ success: false, error: `${resource} æœªæ‰¾åˆ°` });
            }
        } catch (error) {
            res.status(500).json({ success: false, error: error.message });
        }
    });

    // åˆ›å»ºèµ„æº
    app.post(`/api/${resource}`, async (req, res) => {
        try {
            const data = await readJSON(filename);
            const newItem = {
                id: Date.now(),
                ...req.body,
                createdAt: new Date().toISOString()
            };

            data.push(newItem);
            await writeJSON(filename, data);

            res.json({ success: true, data: newItem });
        } catch (error) {
            res.status(500).json({ success: false, error: error.message });
        }
    });

    // æ›´æ–°èµ„æº
    app.put(`/api/${resource}/:id`, async (req, res) => {
        try {
            const data = await readJSON(filename);
            const index = data.findIndex(item => String(item.id) === String(req.params.id));

            if (index !== -1) {
                data[index] = {
                    ...data[index],
                    ...req.body,
                    updatedAt: new Date().toISOString()
                };

                await writeJSON(filename, data);
                res.json({ success: true, data: data[index] });
            } else {
                res.status(404).json({ success: false, error: `${resource} æœªæ‰¾åˆ°` });
            }
        } catch (error) {
            res.status(500).json({ success: false, error: error.message });
        }
    });

    // åˆ é™¤èµ„æº
    app.delete(`/api/${resource}/:id`, async (req, res) => {
        try {
            let data = await readJSON(filename);
            const originalLength = data.length;
            data = data.filter(item => String(item.id) !== String(req.params.id));

            if (data.length < originalLength) {
                await writeJSON(filename, data);
                res.json({ success: true, message: `${resource} å·²åˆ é™¤` });
            } else {
                res.status(404).json({ success: false, error: `${resource} æœªæ‰¾åˆ°` });
            }
        } catch (error) {
            res.status(500).json({ success: false, error: error.message });
        }
    });

    // æ‰¹é‡æ“ä½œ
    app.post(`/api/${resource}/batch`, async (req, res) => {
